CREATE TABLE default.blob_submitter_local\n(\n    `updated_date_time` DateTime COMMENT \'Timestamp when the record was last updated\' CODEC(DoubleDelta, ZSTD(1)),\n    `address` FixedString(66) COMMENT \'Ethereum address of the blob submitter\' CODEC(ZSTD(1)),\n    `name` String COMMENT \'Name of the blob submitter\' CODEC(ZSTD(1)),\n    `meta_network_name` LowCardinality(String) COMMENT \'Ethereum network name\'\n)\nENGINE = ReplicatedReplacingMergeTree(\'/clickhouse/{installation}/{cluster}/default/tables/blob_submitter_local/{shard}\', \'{replica}\', updated_date_time)\nORDER BY (address, meta_network_name)\nSETTINGS index_granularity = 8192\nCOMMENT 'Contains blob submitter address to name mappings.'\n
